import {useState, useEffect} from "react";

const API_BASE = "http://localhost:3001";

// State management using useState hook
function App() {
    const [todos, setTodos] = useState([]);
    const [popupActive, setPopupActive] = useState(false);
    const [newTodo, setNewTodo] = useState("");
    const [newPriority, setNewPriority] = useState("low");
    const [newDueDate, setNewDueDate] = useState(""); // New state for due date
    const [editPriority, setEditPriority] = useState("low"); // Edit priority
    const [editDueDate, setEditDueDate] = useState(""); // Edit due date

    // Fetch initial list of todos from server then calls GetTodos function 
    useEffect(() => {
        GetTodos();

        console.log(todos);
    }, [])

    /* Fetches todos using fetch API and updates 'todos' states,
    Sends GET request to the server's /todos */
    const GetTodos = () => {
        fetch(API_BASE + "/todos")
            .then(res => res.json())
            .then(data => setTodos(data))
            .catch(err => console.error("Error: ", err))
    };

    /* Sends a PUT request to mark a todo as complete or incomplete  using it's ID 
    and then updates the 'todos' state */ 
    const completeTodo = async id => {
        const data = await fetch(API_BASE + "/todo/complete/" + id)
            .then(res => res.json());

        setTodos(todos => todos.map(todo => {
            if (todo._id === data._id) {
                todo.complete = data.complete;
            }

            return todo;
        }));
    };

    /* Sends a DELETE request to delete a todo based on it's id and
    updates the 'todos' state */
    const deleteTodo = async id => {
        const data = await fetch(API_BASE + "/todo/delete/" + id, {
            method: "DELETE"
        }).then(res => res.json());

        setTodos(todos => todos.filter(todo => todo._id !== data._id))
    };

    /* Sends a POST request to make a new todo, updates 'todos' state
     */
    const addTodo = async () => {
        const data = await fetch(API_BASE + "/todo/new", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                text: newTodo,
                priority: newPriority,
                dueDate: newDueDate,
            }),
        }).then(res => res.json());

        setTodos([...todos, data]);
        setPopupActive(false);
        setNewTodo("");
        setNewPriority("low");
        setNewDueDate("");
    };

    const editTodo = async (id) => {
        const data = await fetch(API_BASE + "/todo/edit/" + id, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                priority: editPriority,
                dueDate: editDueDate, // Include edited due date
            }),
        }).then((res) => res.json());

        setTodos((todos) =>
            todos.map((todo) => {
                if (todo._id === data._id) {
                    return data;
                }
                return todo;
            })
        );
    };

    // Function for sorting
    const sortTodos = async () => {
        const response = await fetch(API_BASE + "/todos/sort", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                sortCriteria: newPriority, // Pass the selected sorting criteria
                ascending: false, // Set to true for ascending, false for descending
                sortByDueDate: newPriority === "dueDate", // Sort by due date
            }),
        });

        const sortedTodos = await response.json();
        setTodos(sortedTodos);
    };

	return (
		<div className = "App">
            <h1>Checklist</h1>
            <h4>Your Tasks</h4>

            <select
                className = "sort-dropdown"
                onChange = {(e) => {
                    setNewPriority(e.target.value);
                    sortTodos(); // Call the sorting function when an option is selected
                }}
                value={newPriority}
            >
                <option value = "low">Sort by Priority (Low)</option>
                <option value = "high">Sort by Priority (High)</option>
                <option value = "dueDate">Sort by Due Date</option>
            </select>

            <div className = "todos">
                {todos.map((todo) => (
                    <div 
                    className = {"todo " + (todo.complete ? "is-complete" : "")}
                    key = {todo._id} 
                    onClick = {() => completeTodo(todo._id)}>
                        <div className = "checkbox"></div>

                        <div className = "text">{todo.text}</div>

                        <div 
                        className = "delete-todo" 
                        onClick = {() => deleteTodo(todo._id)}>x</div>
                    </div>
                ))}
            </div>

            <div className = "addPopup" onClick = {() => setPopupActive(true)}>+</div>

            {popupActive ? (
                <div className = "popup">
                    <div className = "closePopup" onClick = {() => setPopupActive(false)}>x</div>
                    <div className = "content">
                        <h3>Add Task</h3>
                        {newTodo}
                        <input 
                            type = "text" 
                            className = "add-todo-input" 
                            onChange = {e => setNewTodo(e.target.value)}
                            value = {newTodo} 
                        />
                         <select
                            className="add-todo-priority"
            onChange={(e) => setNewPriority(e.target.value)}
            value={newPriority}
        >
            <option value="low">Low Priority</option>
            <option value="medium">Medium Priority</option>
            <option value="high">High Priority</option>
        </select>
        <input
            type="date"
            className="add-todo-date" // Add appropriate class for styling
            onChange={(e) => setNewDueDate(e.target.value)}
            value={newDueDate}
        />
                        <div className = "button" onClick = {addTodo}>Create Task</div>
                    </div>
                </div>
                ) : ''}
		</div>
	);
}

export default App;
